mapper.select:
SELECT  FROM wp_post
WHERE  1 = 1



    AND post_date <= #{date}



    AND post_author = #{userId}

ORDER BY post_date DESC LIMIT ${pager.offset,pager.pageSize}

SELECT COUNT(id) FROM (
SELECT  FROM wp_post

    WHERE post_date <= #{date}
    ORDER BY post_date DESC

) AS T

SELECT  FROM wp_post


      post_author = #{postAuthor,jdbcType=BIGINT}


      AND post_date = #{postDate,jdbcType=TIMESTAMP}


      AND  post_date_gmt = #{postDateGmt,jdbcType=TIMESTAMP}


      AND post_status = #{postStatus,jdbcType=VARCHAR}


      AND comment_status = #{commentStatus,jdbcType=VARCHAR}


      AND ping_status = #{pingStatus,jdbcType=VARCHAR}


      AND post_password = #{postPassword,jdbcType=VARCHAR}


      AND post_name = #{postName,jdbcType=VARCHAR}


      AND post_modified = #{postModified,jdbcType=TIMESTAMP}


      AND  post_modified_gmt = #{postModifiedGmt,jdbcType=TIMESTAMP}


      AND  post_parent_id = #{postParentId,jdbcType=BIGINT}


      AND  guid = #{guid,jdbcType=VARCHAR}


      AND  menu_order = #{menuOrder,jdbcType=INTEGER}


      AND post_type = #{postType,jdbcType=VARCHAR}


      AND post_mime_type = #{postMimeType,jdbcType=VARCHAR}


      AND comment_count = #{commentCount,jdbcType=BIGINT}


      AND post_title LIKE concat('%',#{postTitle,jdbcType=LONGVARCHAR},'%')
